INSERT INTO `news` (`id`, `name`, `uri`, `date`, `doDate`, `smallText`, `text`, `title`, `metaK`, `metaD`, `vis`, `image`) VALUES
(6, 'Результаты I международного вокального конкурса "World Music Heritage" 2019, прошедшего 1-2 февраля 2019', 'performance', '2019-02-03 17:42:00', 1, '', '<p><strong>Победитель конкурса - <u>ХО СЯОЦЭНЬ</u></strong></p>\r\n\r\n<p><strong>Младшая возрастная группа</strong></p>\r\n\r\n<p>1 место -&nbsp; Адамян Элиза&nbsp;<br />\r\n2 место -&nbsp; Строганова Александра<br />\r\n3 место -&nbsp; Перовская Полина&nbsp;</p>\r\n\r\n<p><strong>Средняя возрастная группа</strong></p>\r\n\r\n<p>1 место -&nbsp; Пыжова Екатерина&nbsp;<br />\r\n2 место -&nbsp; Андреева Мария<br />\r\n3 место -&nbsp; Алтунина Маргарита</p>\r\n\r\n<p><strong>Старшая возрастная группа</strong></p>\r\n\r\n<p>1 место -&nbsp; Хо Сяоцэнь<br />\r\n2 место -&nbsp; Авакян Мария<br />\r\n3 место -&nbsp; Райская Алиcа</p>\r\n\r\n<p><em><strong>Дипломанты конкурса</strong></em></p>\r\n\r\n<p>Шпилейко Елизавета<br />\r\nРытиков Федор</p>\r\n\r\n<p><em><strong>Специальный диплом за лучшее исполнение итальянской арии&nbsp;</strong></em></p>\r\n\r\n<p>Кочарян Вазген</p>\r\n\r\n<p><em><strong>За лучшее исполнение камерного произведения</strong></em></p>\r\n\r\n<p>Сымон Елена<br />\r\nПавлова Виолетта</p>\r\n', '', '', '', 0, 'award2.jpg'),
(7, 'Специальная номинация "Лучшее исполнение старинной музыки"', 'old', '2019-06-10 13:08:00', 1, '', '<p><strong>ВНИМАНИЕ КОНКУРСАНТАМ!&nbsp;</strong></p>\r\n\r\n<p><strong>По решению оргкомитета и жюри лауреаты&nbsp;конкурса&nbsp;1 степени&nbsp;в&nbsp;номинации &quot;Вокальное искусство&quot;, &quot;Инструментальное искусство&quot;, &quot;Ансамбли&quot;, &quot;Искусство композитора&quot; могут получить возможность выступить в&nbsp;ГОСУДАРСТВЕННОМ КРЕМЛЕВСКОМ ДВОРЦЕ&nbsp;с сольным номером.</strong></p>\r\n\r\n<p><strong>Решение о выступлении&nbsp;лауреатов будет приниматься после подведения итогов конкурса. Выступающие&nbsp;будут оповещены о решении жюри и оргкомитета накануне.&nbsp;</strong></p>\r\n', '', '', '', 1, 'star.jpg'),
(8, 'Результаты I международного музыкального конкурса "World Music Heritage. Young Talent"', 'award', '2019-04-14 20:04:00', 1, '', '<h3><span style="color: rgb(255, 0, 0);"><strong><img alt="" src="/images/ко40.png" style="width: 40px; height: 40px;" />ГРАН ПРИ И ПОБЕДИТЕЛЬ КОНКУРСА - Мкртичян Сергей Артурович</strong></span></h3>\r\n\r\n<p><span style="color: rgb(255, 0, 0);"><strong>ПРИГЛАШАЮТСЯ К ВЫСТУПЛЕНИЮ В КРЕМЛЕВСКОМ ДВОРЦЕ:</strong></span></p>\r\n\r\n<p>1. Дворцов Денис Юрьевич</p>\r\n\r\n<p>2. Ансамбль саксофонистов ДШИ им. Балакирева под упр. Михаила Бирюкова</p>\r\n\r\n<div>3. Мкртичян Александра Артуровна</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>4. Мкртичян Сергей Артурович</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>5. Хускивадзе-Деева Софья</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>Номинация &quot;ФОРТЕПИАНО&quot;</h3>\r\n\r\n<h4><br />\r\n<strong>Группа (А) до 7 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Фудин Аркадий Юрьевич</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Стародубовский Иван Валерьевич</p>\r\n\r\n<p>Лауреат 3 степени Талалай Марта Петровна</p>\r\n\r\n<h4><br />\r\n<strong>Группа (B)&nbsp;от 8 до 13 лет (по решению жюри премии разделены в рамках&nbsp;дополнительных возрастных категорий)</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Искоростенский Артур Фараевич, Стародубовский Серафим Валерьевич</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Пойда Оскар Артурович,&nbsp;Козлова Екатерина Сергеевна&nbsp;</p>\r\n\r\n<p>Лауреат 3 степени Комарова Софья Дмитриевна, Галич Анна Владимировна</p>\r\n\r\n<h4><br />\r\n<strong>Группа (С) от 14 до 18 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Поляцковой Кирилл Владимирович</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Железняков Лев Андреевич</p>\r\n\r\n<p>Лауреат 3 степени Голубева Екатерина Викторовна&nbsp;</p>\r\n\r\n<h4><strong>Группа (D) от 19&nbsp;до 25 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Дворцов Денис Юрьевич</p>\r\n\r\n<p>Лауреат 3 степени&nbsp;Михеева Екатерина Константиновна</p>\r\n\r\n<h4><strong>ДИПЛОМАНТЫ:</strong></h4>\r\n\r\n<p>Рум Даниил Сергеевич</p>\r\n\r\n<p>Пишванова Елизавета Алексеевна</p>\r\n\r\n<p>Сото Синтия</p>\r\n\r\n<p>Нго Хью Хань Линь</p>\r\n\r\n<p>Цуркан Ева</p>\r\n\r\n<h3>Номинация &quot;АКАДЕМИЧЕСКИЙ ВОКАЛ&quot;</h3>\r\n\r\n<h4><strong>Группа (А) до 7 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Удельнова Анна Алексеевна</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Захарян Мария Владиславовна</p>\r\n\r\n<p>Лауреат 3 степени&nbsp;Казакова Анастасия</p>\r\n\r\n<h4><strong>Группа (B)&nbsp;от 8 до 13 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Правилова Полина Борисовна</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Мацкевич Анна Степановна</p>\r\n\r\n<p>Лауреат 3 степени&nbsp;Лукина Юлия Сергеевна, Бродецкая Александра Алексеевна</p>\r\n\r\n<h4><br />\r\n<strong>Группа (С) от 14 до 18 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени Худавердян Жанна Арменовна</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Лебедева Екатерина Дмитриевна</p>\r\n\r\n<p>Лауреат 3 степени &nbsp;Фирстова Вероника Юрьевна</p>\r\n\r\n<h4><strong>Группа (D) от 19&nbsp;до 25 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Горонкова Алевтина Олеговна</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Горовая Кристина Борисовна</p>\r\n\r\n<p>Лауреат 3 степени&nbsp;Якобидзе Георгий Гелович</p>\r\n\r\n<p>Дипломанты:</p>\r\n\r\n<p>Фридман Виктория Марковна</p>\r\n\r\n<p>Басевич Дмитрий Евгеньевич</p>\r\n\r\n<p>Аржанова Анастасия Павловна</p>\r\n\r\n<h3>Номинация &quot;Искусство композитора&quot;&nbsp;</h3>\r\n\r\n<p>Лауреат 1 степени&nbsp;Мкртичян Александра Артуровна</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Голубчик Павел Петрович</p>\r\n\r\n<p>Лауреат 3 степени&nbsp;Загатов Лев Константинович</p>\r\n\r\n<h3>Номинация &quot;ДУХОВЫЕ&quot;</h3>\r\n\r\n<h4><strong>Группа (А) до 7 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Яровой Филипп Андреевич</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Дороговцева Варвара Владимировна</p>\r\n\r\n<p>Лауреат 3 степени&nbsp;Васильева София Романовна</p>\r\n\r\n<h4><br />\r\n<strong>Группа (B)&nbsp;от 8 до 13 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Куракина Василиса Алексеевна,&nbsp;Старых Арсений Вячеславович</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Плеханов Глеб&nbsp;Вадимович</p>\r\n\r\n<p>Лауреат 3 степени&nbsp;Устинова Варвара Алексеевна</p>\r\n\r\n<h4><strong>Группа (С) от 14 до 18 лет</strong></h4>\r\n\r\n<p>Лауреат 3 степени Свицын Адриан Олегович</p>\r\n\r\n<p>Дипломанты:</p>\r\n\r\n<p>Тритенко Анастасия Евгеньевна&nbsp;</p>\r\n\r\n<p>Мухамеджанова Алиса Ринатовна</p>\r\n\r\n<p>Латыпов Рафаэль Вечиславович&nbsp;</p>\r\n\r\n<p>Ивановская Олеся</p>\r\n\r\n<h3>Номинация &quot;СТРУННЫЕ&quot;&nbsp;</h3>\r\n\r\n<h4><strong>Группа (А) до 7 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени Соколова Василиса Алексеевна</p>\r\n\r\n<p>Лауреат 2 степени Плеханова Екатерина Вадимовна</p>\r\n\r\n<p>Лауреат 3 степени Терно Дарья Максимовна</p>\r\n\r\n<h4><strong>Группа (B)&nbsp;от 8 до 13 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени Яковлев Аркадий Геннадьевич</p>\r\n\r\n<p>Лауреат 2 степени Бовина Варвара Владимировна</p>\r\n\r\n<h4><strong>Группа (С) от 14 до 18 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени Хускивадзе-Деева Софья</p>\r\n\r\n<p>Лауреат 2 степени Кришчян Артур Ашотович</p>\r\n\r\n<p>Лауреат 3 степени Седова Диана Константинова, Анчугин Матвей Дмитривевич</p>\r\n\r\n<h4><strong>Группа (D) от 19&nbsp;до 25&nbsp;лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени Шиганова Марина Дмитриевна</p>\r\n\r\n<p>Лауреат 2 степени Боррего Карбонель Ивет Мария</p>\r\n\r\n<p>Лауреат 3 степени Засыпкин Никита Сергеевич</p>\r\n\r\n<p>Дипломанты:&nbsp;</p>\r\n\r\n<p>Свиридова Аглая Алексеевна</p>\r\n\r\n<p>Белова Станислава Олеговна</p>\r\n\r\n<h3>Номинация &quot;Ансамль&quot;</h3>\r\n\r\n<p>Лауреат 1 степени&nbsp;Ансамбль саксофонистов ДШИ им. Балакирева под упр. Михаила Бирюкова</p>\r\n\r\n<p>Лауреат 2 степени&nbsp;Вокальный ансамль &quot;Забавы&quot;</p>\r\n\r\n<p>Лауреат 3 степени Ансамбль скрипачей &quot;Виват&quot;</p>\r\n\r\n<h3>Номинация &quot;Эстрадный вокал&quot;</h3>\r\n\r\n<h4><strong>Группа (А) до 7 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Кузьмина Полина Сергеевна&nbsp;</p>\r\n\r\n<p>Лауреат 2 степени Чижова Майя Витальевна</p>\r\n\r\n<p>Лауреат 3 степени Семенова Есения</p>\r\n\r\n<h4><br />\r\n<strong>Группа (B)&nbsp;от 8 до 13 лет</strong></h4>\r\n\r\n<p><strong>Лауреат 1 степени:&nbsp;</strong></p>\r\n\r\n<p>Нурлан Габил оглы</p>\r\n\r\n<p><strong>Лауреат 2 степени:&nbsp;</strong></p>\r\n\r\n<p>Лукина Юлия Сергеевна</p>\r\n\r\n<p>Чисникова Ирина Геннадьевна</p>\r\n\r\n<p>Синица Арсений Владимирович,&nbsp;</p>\r\n\r\n<p>Гайдар Иванна Святославовна&nbsp;</p>\r\n\r\n<p>Веденеева Василиса Игоревна&nbsp;</p>\r\n\r\n<p>Степанова Полина Александровна</p>\r\n\r\n<p><strong>Лауреат 3 степени:&nbsp;</strong></p>\r\n\r\n<p>Михай Савелий Дмитриевич</p>\r\n\r\n<p>Кубикина Виктория Владимировна</p>\r\n\r\n<p>Страх&nbsp;Вероника Дмитриевна</p>\r\n\r\n<p>Соболева Мария Александровна</p>\r\n\r\n<h4><br />\r\n<strong>Группа (С) от 14 до 18 лет</strong></h4>\r\n\r\n<p><strong>Лауреат 1 степени</strong></p>\r\n\r\n<p>Черепанова Анна Андреевна</p>\r\n\r\n<p><strong>Лауреат 2 степени</strong>&nbsp;</p>\r\n\r\n<p>Гукасян Эрика Эвриковна</p>\r\n\r\n<p>Нистеровская Ульяна Витальевна</p>\r\n\r\n<p>Межевич Родион Леонидович</p>\r\n\r\n<p><strong>Лауреат 3 степени</strong></p>\r\n\r\n<p>Сперанская Майя&nbsp;</p>\r\n\r\n<p>Фирстова Вероника Юрьевна</p>\r\n\r\n<h4><br />\r\n<strong>Группа (D) от 19&nbsp;до 25 лет</strong></h4>\r\n\r\n<p>Лауреат 1 степени&nbsp;Дуэт Грек Елизавета и Муратова Анжелика</p>\r\n\r\n<p>Лауреат 2 степени Харабурдина Алла Борисовна&nbsp;</p>\r\n', '', '', '', 0, 'award4.jpg'),
(10, 'Специальные призы от членов жюри из Испании и Армении!', 'special-prize', '2019-04-01 23:06:00', 1, '', '<p>УЧРЕЖДЕНЫ СПЕЦИАЛЬНЫЕ ПРИЗЫ ОТ ЧЛЕНОВ&nbsp;ЖЮРИ ИЗ ИСПАНИИ И АРМЕНИИ!</p>\r\n', '', '', '', 0, 'spec1.jpg'),
(11, 'Специальные награды для участников', 'special-prize', '2019-04-15 12:15:00', 1, '', '<div>Группа А - спецприз &quot;Надежда&quot;</div>\r\n\r\n<div>Группа B - спецприз &quot;Искренность&quot;</div>\r\n\r\n<div>Группа C - спецприз &quot;Мастерство&quot;</div>\r\n\r\n<div>Группа D - спецприз &quot;Профессионализм&quot;</div>\r\n\r\n<div>&nbsp;</div>\r\n', '', '', '', 1, 'star2.jpg');

