CREATE TABLE IF NOT EXISTS `notes` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `date` datetime NOT NULL,
  `vis` tinyint(1) NOT NULL default '0',
  `name` tinytext character set cp1251 NOT NULL,
  `uri` tinytext character set cp1251 NOT NULL,
  `text` text character set cp1251 NOT NULL,
  `image` tinytext character set cp1251 NOT NULL,
  `ordr` int(11) NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

