  `name` tinytext NOT NULL,
  `text` text NOT NULL,
  `image` tinytext NOT NULL,
  `profession` varchar(255) default NULL,
  `date` datetime NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;

--
-- Дамп данных таблицы `recomendations`
