INSERT INTO `recomendations` (`id`, `vis`, `name`, `text`, `image`, `profession`, `date`) VALUES
(1, 1, 'Петрова Анна', 'Многие говорят, что у них нет способности петь, нет голоса или «медведь наступил на ухо». На самом же деле, петь может любой, но каждому из нас природа дает разный первоначальный певческий багаж.', '004.jpg', 'народная артистка России', '2016-08-01 15:06:00'),
(2, 1, 'Петрова Анна', 'Многие говорят, что у них нет способности петь, нет голоса или «медведь наступил на ухо». На самом же деле, петь может любой, но каждому из нас природа дает разный первоначальный певческий багаж.', '005.jpg', 'народная артистка России', '2016-08-01 15:07:00');

