(82, 1, -5, 0, 'Мастер классы', 'master-klass', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:1000px;">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://young.world-music-heritage.ru/jury/roman-zorkin/"><img alt="" src="/images/romsm.jpg" style="width: 350px; height: 495px;" /></a></td>\r\n			<td><a href="http://young.world-music-heritage.ru/jury/alisa-gitcba/"><img alt="" src="/images/alsm.jpg" style="width: 350px; height: 495px;" /></a></td>\r\n			<td><a href="http://young.world-music-heritage.ru/jury/syergyey-chyechyetko/"><img alt="" src="/images/sersm.jpg" style="width: 350px; height: 495px;" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">&nbsp;</td>\r\n			<td><a href="http://young.world-music-heritage.ru/jury/asya-syelyutina/"><img alt="" src="/images/assm.jpg" style="width: 350px; height: 495px;" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n', '', '', '', 0, 1, 0, '', '', ''),
