INSERT INTO `texts` (`id`, `name`, `text`, `ordr`) VALUES
(1, '', '<p><span style=\\"color: rgb(0, 0, 0); font-family: -apple-system, BlinkMacSystemFont, Roboto, &quot;Open Sans&quot;, &quot;Helvetica Neue&quot;, sans-serif;\\">Открыт прием заявок на участие во I&nbsp;Международном конкурсе-фестивале исполнителей г. Москва&nbsp;18-21 февраля 2017 г по следующим номинациям:&nbsp;</span></p>\r\n', -2),
(2, 'Программные требования', '<p>Свободная программа, включающая произведения разных стилей с обязательным исполнением одного или нескольких произведений Франца Шуберта (в оригинальном виде или в обработке; возможно исполнение отдельных частей произведения). Концертмейстер вправе участвовать в номинации &laquo;Искусство концертмейстера&raquo;. Возможно участие в Конкурсе как со своим концертмейстером, так и с концертмейстером, предоставленным Оргкомитетом Конкурса.</p>\r\n', 0),
(3, '​Возрастные категории и время исполнения', '<p>Cекция I. Инструментальное исполнительское искусство<br />\r\nВсе номинации:<br />\r\n1 категория: до 12 лет (до 10 минут);<br />\r\n2 категория: с 13 лет до 16 лет (10-12 минут);<br />\r\n3 категория: с 17 лет до 22 лет (12-15 минут);<br />\r\n4 категория: с 23 до 27 лет (15-20 минут);<br />\r\n5 категория: с 28 лет возраст не ограничен (20-25 минут).<br />\r\nСекция II. Вокальное искусство<br />\r\nВсе номинации:<br />\r\n1 категория: до 14 лет (до 10 минут);<br />\r\n2 категория: с 15 до 19 лет (10-15 минут);<br />\r\n3 категория: с 20 до 27 лет (15-20 минут);<br />\r\n4 категория: с 28 лет возраст не ограничен (20-25 минут).</p>\r\n', -1);

