  `login` varchar(100) character set cp1251 NOT NULL,
  `password` varchar(255) character set cp1251 NOT NULL,
  `mail` varchar(100) character set cp1251 NOT NULL,
  `rule` int(10) NOT NULL default '4',
  `family` tinytext character set cp1251 NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

--
-- Дамп данных таблицы `users`
--

